mapper.insert:
INSERT INTO submit (stud_id, problem_no, code, lang, state, time, memory, score)
VALUES (#{studId}, #{problemNo}, #{code}, #{lang}, #{state}, #{time}, #{memory}, #{score})
WHERE id = #{studId}

mapper.update:
UPDATE submit
SET score = #{score}
WHERE id = #{studId}

mapper.select:
SELECT score
FROM submit
WHERE stud_id = #{studId}
